/**
 * 跳转到登录界面
 * @param response
 * @returns 不需要跳转时返回请求内容
 */
function tologin(response){
	if(response.length<100&&response.indexOf("请重新登录") > 0) {
		var json=$.parseJSON(response);
		var url=json.url;//.split("?")[0];
		localStorage.backurl=window.location.href;
		if(com_id){
			if(url.indexOf("?")>=0){
				if(url.indexOf("com_id")<0){
					url=url+"&com_id="+com_id
				}
			}else{
				url=url+"?com_id="+com_id
			}
		}
		window.location.href=url;  
		return "";  
	}else{
		//如果没有超时直接返回  
		return response;  
	}
}
/**
 * 过滤ajax请求,判断页面是否失效
 */
function URLFiltering(){
	$.ajaxSetup({
		contentType : "application/x-www-form-urlencoded;charset=utf-8",
		cache:false , 
		dataFilter:function(response){
			return tologin(response);  
		},
		beforeSend:function(xhr,textStatus){
//			if(textStatus.url.indexOf("_")<0){
//				textStatus.url=textStatus.url+"&ver="+Math.random();
//			}
			var responseText = xhr.responseText;
			if (responseText){
				tologin(responseText); 
			}
		},
		success: function (xhr) {
			findflag=0;  
		},
		error: function(jqXHR, textStatus, errorMsg){
			pop_up_box.loadWaitClose();
			findflag=0;
		},
		complete : function(XMLHttpRequest, textStatus) {
			var responseText = XMLHttpRequest.responseText;
			findflag=0;
			if (responseText) {
				tologin(responseText); 
			}
		}
	});
}
///16进制字符串转中文
function decode(s) {
	if(s){
		var arr=s.split(",");
		if(arr.length>1){
			var str="";
			for (var i = 0; i < arr.length; i++) {
				if (arr[i]!="") {
					str+=String.fromCharCode(parseInt(arr[i], 16));
				}
			}
			return str;
		}else{
			return s;
		}
	}else{
		return "";
	}
//    return s.replace(/.{4}/g, function($) {
//        return String.fromCharCode(parseInt($, 16));
//    });
}
