//字符串去空格
String.prototype.trim = function() {
	return this.replace(/^\s\s*/, '').replace(/\s\s*$/, '');
}
function initNumInput() {
	//字母与数字
	$("input[data-number='zimu'],input[data-type='zimu']").bind("input propertychange blur", function() {
		var val=$(this).val().replace(/^(\-)*(\d+)\.(\d\d).*$/,'$1$2.$3');
		 $(this).val(val);
	});
	$("input").focus(function(){
		$(this).select();
	});
	$("input[data-number]").change(function() {
		if ($.trim($(this).val()) == "") {
			$(this).val("");
		}
	});
	//只能输入整数数字
	$("input[data-number='num'],input[data-num='num']").bind("input propertychange blur", function() {
		num($(this));
	}); 
	function num(t){
		var reg = new RegExp("^[0-9]*$");
		var val=$.trim(t.val());
		if (!reg.test(val)) {
			t.val(val.substring(0, val.length - 1));
			num(t);
		}
	}
	//只能输入数字,包括小数
	$("input[data-number='num2'],input[data-num='num2']").bind("input propertychange blur", function() {
		num2($(this));
	});
	function num2(t){
		var reg = new RegExp("^[0-9.]*$");
		if(!reg.test(t.val().trim())){
			t.val(t.val().substring(0, t.val().length - 1));
			if(!reg.test(t.val().trim())){
				num2(t);
			}
		}
	}
	//只能输入数字,包括负数
	$("input[data-number='fu'],input[data-num='fu']").bind("input propertychange blur", function() {
		numfu($(this));
	}); 
	function numfu(t){
		var reg = new RegExp("^[0-9-]*$");
		if (!reg.test(t.val().trim())) {
			t.val(t.val().substring(0, t.val().length - 1));
			numfu(t);
		}
	}
	//只能输入网址
	$("input[data-num='url']").bind("change", function() {
		if (!IsURL($(this).val())) {
			alert("网址输入不合法,请重新输入!");
			$(this).val("");
			$(this).focus();
		}
	});
}
function IsURL(str_url) {
	var strRegex = "^((https|http|ftp|rtsp|mms)?://)"
			+ "?(([0-9a-z_!~*'().&=+$%-]+: )?[0-9a-z_!~*'().&=+$%-]+@)?" // ftp的user@
			+ "(([0-9]{1,3}\.){3}[0-9]{1,3}" // IP形式的URL- 199.194.52.184
			+ "|" // 允许IP和DOMAIN（域名）
			+ "([0-9a-z_!~*'()-]+\.)*" // 域名- www.
			+ "([0-9a-z][0-9a-z-]{0,61})?[0-9a-z]\." // 二级域名
			+ "[a-z]{2,6})" // first level domain- .com or .museum
			+ "(:[0-9]{1,4})?" // 端口- :80
			+ "((/?)|" // a slash isn't required if there is no file name
			+ "(/[0-9a-z_!~*'().;?:@&=+$,%#-]+)+/?)$";
	var strRegex = /http(s)?:\/\/([\w-]+\.)+[\w-]+(\/[\w- .\/?%&=]*)?/;
	var re = new RegExp(strRegex);
	// re.test()
	if (re.test(str_url)) {
		return (true);
	} else {
		return (false);
	}
}
function IsPC(){
    var userAgentInfo = navigator.userAgent;  
    var Agents = new Array("Android", "iPhone", "SymbianOS", "Windows Phone", "iPad", "iPod");    
    var flag = true;    
    for (var v = 0; v < Agents.length; v++) {    
        if (userAgentInfo.indexOf(Agents[v]) > 0) { flag = false; break; }    
    }    
    return flag;
 }
/**
 * 只能输入数字,字母,下划线
 * 
 * @param t
 */
function datanum(t) {
	var reg = new RegExp("^[0-9a-zA-Z_./:]{1,}$");
	if (t) {
		var val = $(t).val();
		if (val && !reg.test($.trim(val))) {
			$(t).val($.trim(val.substring(0, val.length - 1)));
			datanum(t);
		}
	}
}
