var code = 100;
var timeoutId="";
/**
 * 获取验证码倒计时
 * @param nowCode
 *            当前剩余时间
 */
function getCode(nowCode) {
	if (nowCode) {
		code = nowCode;
	}
	var get_code = $("#get_code");
	code = code - 1;
	if (code == 0) {
		get_code.removeAttr("disabled");
		get_code.html("获取验证码");
		clearTimeout(timeoutId);
		code = 100;
		return;
	}
	get_code.html( code + "秒重新获取");
	timeoutId = setTimeout("getCode()", 1000);
}
/**
 * 将json数据转换为get提交的参数
 * parseParam(obj);
 */
var parseParam=function(param, key){
		    var paramStr="";
		    if(param instanceof String||param instanceof Number||param instanceof Boolean){
		        paramStr+="&"+key+"="+encodeURIComponent(param);
		    }else{
		        $.each(param,function(i){
		            var k=key==null?i:key+(param instanceof Array?"["+i+"]":"."+i);
		            paramStr+='&'+parseParam(this, k);
		        });
		    }
		    return paramStr.substr(1);
		};
function closeWebPage() {
	if (navigator.userAgent.indexOf("MSIE") > 0) {
		if (navigator.userAgent.indexOf("MSIE 6.0") > 0) {
			window.opener = null;
			window.close();
		} else {
			window.open('', '_top');
			window.top.close();
		}
	} else if (navigator.userAgent.indexOf("Firefox") > 0) {
		window.location.href = 'about:blank ';
	} else {
		window.opener = null;
		window.open('', '_self', '');
		window.close();
	}
}
function getItemIds() {
	var itemIds = $.cookie("itemIds");
	if (itemIds == "null" || !itemIds) {
		itemIds = new Array();
	} else {
		var ids = itemIds.split("-");
		itemIds = ids;
	}
	return itemIds;
}
function saveItemIds(itemIds) {
	$.cookie("itemIds", itemIds.join("-"), {
		expires : 7
	});
}
