function loadIframe(url){
	$("body").css("padding-top","0");
	var winHeight=0;
	if (window.innerHeight)
		winHeight = window.innerHeight;
	else if ((document.body) && (document.body.clientHeight))
		winHeight = document.body.clientHeight;
	winHeight=winHeight-5;
	pop_up_box.loadWait();
	var iframe=$('<iframe src="'+url+'" width="100%" height="'+winHeight+'" frameborder="0" scrolling="yes" marginwidth="0" marginheight="0"></iframe>');
	$("#editpage").html(iframe);
	$(iframe,window.parent.document).bind("load",function(){
		pop_up_box.loadWaitClose();
	})
}
