 ///////////json数组处理
/**
 * 按照key值排序,合并json数组
 * @params ls 源数组
 * @params key 合并key值
 * @params 数据源结构
 * 	var ls=[{"gysid":"g1","list":[{"gl1":"gl1"},{"g1":"gl2"}]},
 * {"gysid":"g2","list":[{"g22":"g22"},{"g22":"g22"}]},
 * {"gysid":"g1","list":[{"gl1":"gl2"},{"g1":"glg"}]}];
 * @returns 合并后的数组
 */
function hebingjsonlist(ls,key){
	ls=JsonSort(ls,key);//排序
	var gyid;
	var lss=[];
	for (var i = 0; i < ls.length; i++) {
		var gid=ls[i][key];
		if(!gyid){
			gyid=gid;
			lss.push($.parseJSON(ls[i]));
		}else if(gyid==gid){
			var gys=ls[isHasElement(lss,ls[i][key])];
			for (var j = 0; j < ls[i].list.length; j++) {
				gys.list.push($.parseJSON(ls[i].list[j]));
			}
		}else{
			gyid=gid;
			lss.push($.parseJSON(ls[i]));
		}
	}
	return lss;
}
/**
 * 按照key值进行排序
 * @param arr 源数组
 * @param key 排序值
 * @returns 返回排序后的数组
 */
function JsonSort(json,key){
    for(var j=1,jl=json.length;j < jl;j++){
        var temp = json[j],
            val  = temp[key],
            i    = j-1;
        while(i >=0 && json[i][key]>val){
            json[i+1] = json[i];
            i = i-1;   
        }
        json[i+1] = JSON.stringify(temp);

    }
    return json;
}
/**
 * 判断指定字符串在json字符串中的值
 * @param arrs 源数组
 * @param value 查询的值
 * @returns 返回所在位置的索引,失败返回-1
 */
function isHasElement(arrs,value){
	for (var i = 0; i < arrs.length; i++) {
		var arr=arrs[i];
		var str = JSON.stringify(arr);
		var index = str.indexOf(value);
		if(index >= 0){
			return i;
		}
	}
	return -1;
}
/**
 * 字符串全部替换
 * @param str 数据源
 * @param replaceStr 替换目标内容
 * @param destStr 替换结果内容
 * @returns 替换后的结果
 */
function replaceAll(str,replaceStr,destStr){
	if(!destStr){
		destStr="";
	}
	return str.replace(new RegExp(replaceStr,'gm'),destStr);
}
