/**
 * 汇总计算
 * @param trcount 汇总行对象
 * @param ni 第几个表格
 * @param dataName 数据列对应字段名称
 * @param xiao 小数点位数
 */
function getSumNum(trcount,ni,dataName,xiao){
	var tr=$("tbody:eq("+ni+") tr");
	var zplanum=0;
	var len=$("thead:eq("+ni+") th").length;
	var thindex=$("thead:eq("+ni+") th").index($("thead:eq("+ni+") th[data-name='"+dataName+"']"));
	for (var j = 0; j < tr.length; j++) {
		var n=$.trim($(tr[j]).find("td:eq("+thindex+")").html());
		var hui=$.trim($(tr[j]).find("td:eq(0)").html());
		if(hui!="汇总"){
			if(n==""){
				n="0";
			}
			var num= parseFloat(n);
			zplanum=zplanum+num;
		}
	}
	if(xiao==undefined){
		xiao=2;
	}
	if(xiao==2){
		trcount.find("td:eq("+thindex+")").html(numformat2(zplanum));
	}else{
		trcount.find("td:eq("+thindex+")").html(numformat(zplanum,xiao));
	}
}
